pragma solidity 0.8.4;
import './QuadMath.sol';



/**
 * @title StakeMath
 * @notice a library for performing staking operations
 * @dev #TODO
 */
library StakeMath {
    using QuadMath for uint256;

    struct Position {
        uint256 shares;
        uint256 earnings;
    }

    struct State {
        uint256 shares;
        uint256 epsX128;
    }

    /**
     * @notice #TODO
     * @param state the amount to take a percent of
     * @param amount the percent out of 100 to be taken
     * @return res earnings
     * @dev #TODO
     */
    function convertSharesToEarnings(State memory state, uint256 amount) internal pure returns (uint256 res) {
        res = amount.mulDiv(state.epsX128, QuadMath._BINARY128);
    }

    /**
     * @notice #TODO
     * @param state the amount to take a percent of
     * @param amount the percent out of 100 to be taken
     * @return res earnings
     * @dev #TODO
     */
    function convertEarningsToEpsX128(State memory state, uint256 amount) internal pure returns (uint256 res) {
        res = amount.mulDiv(QuadMath._BINARY128, state.shares);
    }

    function applyWithdraw(
        State memory state,
        Position memory pos,
        uint256 amount
    ) internal pure {
        applyRealize(state, pos);
        applyShareDecrease(state, pos, amount);
    }

    function applyDeposit(
        State memory state,
        Position memory pos,
        uint256 amount
    ) internal pure {
        applyRealize(state, pos);
        applyShareIncrease(state, pos, amount);
    }

    function epsFromRewardIncrease(uint256 amount, State memory state) internal pure returns (uint256 res) {
        applyRewardIncrease(state, amount);
        res = state.epsX128;
    }

    /**
     * @notice #TODO
     * @param state the percent out of 100 to be taken
     * @param pos the percent out of 100 to be taken
     * @return res shares
     * @dev #TODO
     */
    function getEarnings(State memory state, Position memory pos) internal pure returns (uint256 res) {
        uint256 unadjusted = convertSharesToEarnings(state, pos.shares);
        require(unadjusted >= pos.earnings, 'SM:EARN:0');
        res = unadjusted - pos.earnings;
    }

    function applyShareIncrease(
        State memory state,
        Position memory pos,
        uint256 amount
    ) internal pure {
        if (amount > 0) {
            uint256 earningsOffset = convertSharesToEarnings(state, amount);
            pos.earnings += earningsOffset;
            pos.shares += amount;
            state.shares += amount;
        }
    }

    function applyShareDecrease(
        State memory state,
        Position memory pos,
        uint256 amount
    ) internal pure {
        require(pos.shares >= amount, 'STAKE:SD:0');
        uint256 earningsOffset = convertSharesToEarnings(state, amount);
        pos.earnings = pos.earnings >= earningsOffset ? pos.earnings - earningsOffset : 0;
        pos.shares -= amount;
        state.shares -= amount;
    }

    /**
     * @dev increases a users total staked shares in a given epoch
     * @param state the user who is adding shares
     * @param pos ed
     * @custom:after funds will be dealt with from returned value
     * @custom:echidna total value of user funds (shares + (converted) earnings) stays the same
     */
    function applyRealize(State memory state, Position memory pos) internal pure {
        uint256 sharesEarned = getEarnings(state, pos);
        pos.earnings = convertSharesToEarnings(state, pos.shares);
        applyShareIncrease(state, pos, sharesEarned);
    }

    // epsX128 is always >= when it started
    function applyRewardIncrease(State memory state, uint256 amount) internal pure {
        require(state.shares > 0, 'STAKE:RI:0');
        state.epsX128 += convertEarningsToEpsX128(state, amount);
    }

    /**
     * @dev increases a users total staked shares in a given epoch
     * @param state the user who is adding shares
     * @param pos ed
     * @custom:after funds will be dealt with from returned value
     * @custom:echidna ethToSend is never greater that tvl and always equals
     */
    // function applyPaperhand(
    //     State memory state,
    //     Position memory pos,
    //     uint256 amount
    // ) internal pure returns (uint256 ethToSend, uint256 earned) {
    //     earned = applyRealize(state, pos);
    //     applyShareDecrease(state, pos, amount);
    //     ethToSend = earned + amount;
    // }

    // users total value stays the same
    // function applyCompound(State memory state, Position memory pos) internal pure returns (uint256 earned) {
    //     earned = applyRealize(state, pos);
    //     applyShareIncrease(state, pos, earned);
    // }
}
