pragma solidity 0.8.4;

/**
 * @title QuadMath
 * @notice a library for performing Quadruple-precision floating-point format math operations
 * @dev #TODO
 */
library QuadMath {
    struct Uint512 {
        uint256 a;
        uint256 b;
    }
    /**
     * Max Value of Quad Percion Binary 128
     * Equal to 340282366920938463463374607431768211456 or 2^128
     */
    // uint256 internal constant _BINARY128 = 0x100000000000000000000000000000000;
    uint256 internal constant _BINARY128 = 0x100000000000000000000000000000000;

    /**
     * @notice #TODO
     * @param base_ the amount to take a percent of
     * @param percent_ the percent out of 100 to be taken
     * @return res precent_ of base_
     * @dev #TODO
     */
    function percent(uint256 base_, uint8 percent_) internal pure returns (uint256 res) {
        require(percent_ <= 100);
        res = mulDiv(base_, percent_ * _BINARY128, 100 * _BINARY128);
    }

    function mulDivRoundingUnsafe(
        uint256 a,
        uint256 b,
        uint256 denominator
    ) internal pure returns (uint256 result) {
        result = mulDiv(a, b, denominator);
        if (mulmod(a, b, denominator) >= denominator / 2) {
            require(result < type(uint256).max);
            result++;
        }
    }

    function mulDivQuad(
        uint256 a,
        uint256 b,
        uint256 denominator
    ) internal pure returns (uint256 result) {
        result = mulDiv(mulDiv(a, _BINARY128, denominator), b, _BINARY128);
    }

    function mulDivQuadUp(
        uint256 a,
        uint256 b,
        uint256 denominator
    ) internal pure returns (uint256 result) {
        result = mulDivRoundingUp(mulDivRoundingUp(a, _BINARY128, denominator), b, _BINARY128);
    }

    /**
     * @notice Calculates ceil(a×b÷denominator) with full precision. Throws if result overflows a uint256 or denominator == 0
     * @param a The multiplicand
     * @param b The multiplier
     * @param denominator The divisor
     * @return result The 256-bit result
     * @dev Credit to Uniswap under MIT license https://github.com/Uniswap/uniswap-v3-core/blob/main/contracts/libraries/FullMath.sol
     */
    function mulDivRoundingUp(
        uint256 a,
        uint256 b,
        uint256 denominator
    ) internal pure returns (uint256 result) {
        result = mulDiv(a, b, denominator);
        if (mulmod(a, b, denominator) > 0) {
            require(result < type(uint256).max);
            result++;
        }
    }

    /**
     * @notice Calculates floor(a×b÷denominator) with full precision. Throws if result overflows a uint256 or denominator == 0
     * @param a The multiplicand
     * @param b The multiplier
     * @param denominator The divisor
     * @return result The 256-bit result
     * @dev Credit to Remco Bloemen under MIT license https://xn--2-umb.com/21/muldiv
     */
    function mulDiv(
        uint256 a,
        uint256 b,
        uint256 denominator
    ) internal pure returns (uint256 result) {
        // 512-bit multiply [prod1 prod0] = a * b
        // Compute the product mod 2**256 and mod 2**256 - 1
        // then use the Chinese Remainder Theorem to reconstruct
        // the 512 bit result. The result is stored in two 256
        // variables such that product = prod1 * 2**256 + prod0
        uint256 prod0; // Least significant 256 bits of the product
        uint256 prod1; // Most significant 256 bits of the product
        assembly {
            let mm := mulmod(a, b, not(0))
            prod0 := mul(a, b)
            prod1 := sub(sub(mm, prod0), lt(mm, prod0))
        }

        // Handle non-overflow cases, 256 by 256 division
        if (prod1 == 0) {
            require(denominator > 0);
            assembly {
                result := div(prod0, denominator)
            }
            return result;
        }

        // Make sure the result is less than 2**256.
        // Also prevents denominator == 0
        require(denominator > prod1);

        ///////////////////////////////////////////////
        // 512 by 256 division.
        ///////////////////////////////////////////////

        // Make division exact by subtracting the remainder from [prod1 prod0]
        // Compute remainder using mulmod
        uint256 remainder;
        assembly {
            remainder := mulmod(a, b, denominator)
        }
        // Subtract 256 bit number from 512 bit number
        assembly {
            prod1 := sub(prod1, gt(remainder, prod0))
            prod0 := sub(prod0, remainder)
        }

        // Factor powers of two out of denominator
        // Compute largest power of two divisor of denominator.
        // Always >= 1.
        // fix for negation braking change in Solidity v8 - https://ethereum.stackexchange.com/a/96646
        uint256 twos = denominator & (~denominator + 1);
        // Divide denominator by power of two
        assembly {
            denominator := div(denominator, twos)
        }

        // Divide [prod1 prod0] by the factors of two
        assembly {
            prod0 := div(prod0, twos)
        }
        // Shift in bits from prod1 into prod0. For this we need
        // to flip `twos` such that it is 2**256 / twos.
        // If twos is zero, then it becomes one
        assembly {
            twos := add(div(sub(0, twos), twos), 1)
        }

        // this unchecked block is required for this to work with Solidity v8
        unchecked {
            prod0 |= prod1 * twos;

            // Invert denominator mod 2**256
            // Now that denominator is an odd number, it has an inverse
            // modulo 2**256 such that denominator * inv = 1 mod 2**256.
            // Compute the inverse by starting with a seed that is correct
            // correct for four bits. That is, denominator * inv = 1 mod 2**4

            uint256 inv = (3 * denominator) ^ 2;
            // Now use Newton-Raphson iteration to improve the precision.
            // Thanks to Hensel's lifting lemma, this also works in modular
            // arithmetic, doubling the correct bits in each step.
            inv *= 2 - denominator * inv; // inverse mod 2**8
            inv *= 2 - denominator * inv; // inverse mod 2**16
            inv *= 2 - denominator * inv; // inverse mod 2**32
            inv *= 2 - denominator * inv; // inverse mod 2**64
            inv *= 2 - denominator * inv; // inverse mod 2**128
            inv *= 2 - denominator * inv; // inverse mod 2**256

            // Because the division is now exact we can divide by multiplying
            // with the modular inverse of denominator. This will give us the
            // correct result modulo 2**256. Since the precoditions guarantee
            // that the outcome is less than 2**256, this is the final result.
            // We don't need to compute the high bits of the result and prod1
            // is no longer required.
            result = prod0 * inv;
        }
        return result;
    }
}
","keccak256":"0x91db32f050f38f38f875b8addbd3975f4006815e4c9c366e4e1f505aba126095","license":"MIT"},"contracts/libraries/StakeMath.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.4;
import './QuadMath.sol';



/**
 * @title StakeMath
 * @notice a library for performing staking operations
 * @dev #TODO
 */
library StakeMath {
    using QuadMath for uint256;

    struct Position {
        uint256 rOwned;
    }

    struct State {
        uint256 tSupply;
        uint256 rSupply;
    }

    /**
     * @notice #TODO
     * @param state the percent out of 100 to be taken
     * @param pos the percent out of 100 to be taken
     * @return res shares
     * @dev #TODO
     */
    function getBalance(State memory state, Position memory pos) internal pure returns (uint256 res) {
        return pos.rOwned > 0 ? _safeRtoT(state, pos.rOwned) : 0;
    }

    /**
     * @notice #TODO
     * @param state the percent out of 100 to be taken
     * @param pos the percent out of 100 to be taken
     * @return res shares
     * @dev #TODO
     */
    function getOwnershipX128(State memory state, Position memory pos) internal pure returns (uint256 res) {
        return pos.rOwned.mulDiv(QuadMath._BINARY128, state.rSupply);
    }

    function _safeRtoT(State memory state, uint256 rAmount) private pure returns (uint256) {
        return rAmount.mulDiv(state.tSupply, state.rSupply);
    }

    function _safeTtoR(State memory state, uint256 tAmount) private pure returns (uint256) {
        return tAmount.mulDiv(state.rSupply, state.tSupply);
    }

    // function _safeRtoTRoundingUnsafe(State memory state, uint256 rAmount) private pure returns (uint256) {
    //     return rAmount.mulDivRoundingUnsafe(state.tSupply, state.rSupply);
    // }

    // function _safeTtoRRoundingUnsafe(State memory state, uint256 tAmount) private pure returns (uint256) {
    //     return tAmount.mulDivRoundingUnsafe(state.rSupply, state.tSupply);
    // }

    function _safeRtoTRoundingUp(State memory state, uint256 rAmount) private pure returns (uint256) {
        return rAmount.mulDivRoundingUp(state.tSupply, state.rSupply);
    }

    function _safeTtoRRoundingUp(State memory state, uint256 tAmount) private pure returns (uint256) {
        return tAmount.mulDivRoundingUp(state.rSupply, state.tSupply);
    }

    function applyShareIncrease(
        State memory state,
        Position memory pos,
        uint256 tAmount
    ) internal pure {
        if (state.rSupply == 0 && state.tSupply == 0) {
            pos.rOwned = tAmount;
            state.rSupply = tAmount;
            state.tSupply = tAmount;
        } else {
            uint256 amountR = _safeTtoR(state, tAmount);
            pos.rOwned += amountR;
            state.rSupply += amountR;
            state.tSupply += tAmount;
        }
    }

    function applyShareDecrease(
        State memory state,
        Position memory pos,
        uint256 tAmount
    ) internal pure {
        uint256 amountR = _safeTtoRRoundingUp(state, tAmount);
        pos.rOwned -= amountR;
        state.rSupply -= amountR;
        state.tSupply -= tAmount;
    }

    function applyRewardIncrease(State memory state, uint256 amount) internal pure {
        state.tSupply += amount;
    }
}
