    modifier isLaunched() {
        require(_launched, 'LAU:IL:0');
        _;
    }

    constructor() {
        _deployer = tx.origin;
        _deployerContract = msg.sender;
    }

    /**
     * @dev #TODO
     */
    function deployer() external view override returns (address) {
        return _deployer;
    }

    /**
     * @dev #TODO
     */
    function launched() external view override returns (bool) {
        return _launched;
    }

