pragma solidity 0.8.4;

import './libraries/Base64.sol';
import './libraries/SeedMath.sol';
import './libraries/Uint.sol';

import './base/Launchable.sol';
import './base/Seedable.sol';
import './base/Epochable.sol';

import './erc721/ERC721.sol';

import './core/interfaces/IDotNugg.sol';
import './interfaces/INuggFT.sol';
import './auction/NuggMinter.sol';
import './auction/NuggSeller.sol';

import './gen/DotNuggGenerated.sol';

import './weth/interfaces/INuggETH.sol';

/**
 * @title Nugg Labs NFT Collection 0 - "NuggFT"
 * @author Nugg Labs - @danny7even & @dub6ix - 2021
 * @notice entrily onchain generative NFT and stakable auction contract
 * @dev this is art
 *
 * Note: epochs correlate directly to tokenIDs
 * Note: no images are stored in their final form - they are generated by view/pure functions at query time completly onchain
 * Note: the block hash corresponding to the start of an epoch is used as the "random" seed
 * Note: epochs are 256 blocks long as block hashes only exist for 256 blocks
 */
contract NuggFT is INuggFT, ERC721, Launchable, DotNuggGenerated, Mutexable {
    using SeedMath for bytes32;
    using Uint256 for uint256;

    uint8 private constant _MAX_FEATURES = 3;

    uint8[] private _featureIDs;

    IDotNugg internal _DOTNUGG;
    INuggETH internal _NUGGETH;
    INuggMinter internal _MINTER;
    INuggSeller internal _SELLER;

    Mutex transfer;

    constructor() ERC721('Nugg Fungable Token', 'NuggFT') {
        transfer = initMutex();
    }

    function onMinterClaim(address minter, uint256 tokenId) external override lock(transfer) {
        require(msg_sender() == address(_MINTER), 'NFT:OMC:0');
        _safeMint(minter, tokenId);
    }

    function onBuyerClaim(address buyer, uint256 tokenId) external override lock(transfer) {
        require(msg_sender() == address(_SELLER), 'NFT:OBC:0');
        _safeTransfer(address(_SELLER), buyer, tokenId, '');
    }

    function _beforeTokenTransfer(
        address,
        address,
        uint256
    ) internal view override {
        require(msg_sender() == address(_MINTER) || msg_sender() == address(_SELLER), 'NFT:BTT:0');
    }

    /**
     * @notice inializes contract outside of constructor
     * @inheritdoc Launchable
     */
    function launch(bytes memory data) public override {
        super.launch(data);
        (address nuggeth, address dotnugg, address minter, address seller) = abi.decode(data, (address, address, address, address));
        _NUGGETH = INuggETH(nuggeth);
        _DOTNUGG = IDotNugg(dotnugg);
        _MINTER = INuggMinter(minter);
        _SELLER = INuggSeller(seller);

        IDotNugg.Collection memory coll = _DOTNUGG.bytesToCollection(_collection());
        for (uint8 i = 0; i < coll.features.length; i++) {
            _featureIDs.push(coll.features[i].id);
        }
    }

    /**
     * @inheritdoc ERC721
     */
    function tokenURI(uint256 tokenId) public view override isLaunched returns (string memory res) {
        require(_MINTER.seedExists(tokenId), 'NUG:TURI:0');
        res = _generateTokenURI(tokenId, _MINTER.getSeed(tokenId).toUint256());
    }

    /**
     * @notice equivilent of tokenURI function, but for only for active epoch as real uri does not exist yet
     */
    function pendingTokenURI() public view override isLaunched returns (string memory res) {
        uint256 id = _MINTER.currentEpochId();
        bytes32 seed = _MINTER.seedExists(id) ? _MINTER.getSeed(id) : _MINTER.calculateCurrentSeed();
        res = _generateTokenURI(id, seed.toUint256());
    }

    /**
     * @notice calcualtes the token uri for a given epoch
     */
    function _generateTokenURI(uint256 epoch, uint256 seed) internal view returns (string memory) {
        string memory uriName = string(abi.encodePacked('NuggFT #', epoch.toString()));
        string memory uriDesc = 'TDB';
        IDotNugg.Base memory base = _getBase(seed);
        IDotNugg.Attribute[] memory attrs = _getAttributes(seed);

        IDotNugg.Display memory display = _DOTNUGG.newDisplay(base, attrs);

        string memory uriImage = Base64.encodeSvg(_DOTNUGG.toSvg(display, 10));

        return
            string(
                abi.encodePacked(
                    Base64.encodeJson(bytes(abi.encodePacked('{"name":"', uriName, '","description":"', uriDesc, '", "image": "', uriImage, '"}')))
                )
            );
    }

    /**
     * @notice gets unique attribtues based on given epoch and converts encoded bytes to object that can be merged
     */
    function _getAttributes(uint256 seed) internal view returns (IDotNugg.Attribute[] memory res) {
        uint16[] memory randomAttributeIDs = new uint16[](30);
        for (uint8 i = 0; i < 30; i++) {
            randomAttributeIDs[i] = uint16((seed >> i) % attributes_.length);
        }

        uint8[] memory selectedFeatureIDCheck = new uint8[](_featureIDs.length);

        res = new IDotNugg.Attribute[](_MAX_FEATURES);

        uint8 selectedAttrIndex = 0;
        for (uint8 i = 0; i < randomAttributeIDs.length; i++) {
            IDotNugg.Attribute memory potentialAttribute = _DOTNUGG.bytesToAttribute(_attributes(randomAttributeIDs[i]));
            if (selectedFeatureIDCheck[potentialAttribute.feature.id] == 0) {
                res[selectedAttrIndex++] = potentialAttribute;
                selectedFeatureIDCheck[potentialAttribute.feature.id] == 1;
            }
            if (selectedAttrIndex == _MAX_FEATURES) {
                break;
            }
        }
    }

    /**
     * @notice gets unique base based on given epoch and converts encoded bytes to object that can be merged
     */
    function _getBase(uint256 seed) internal view returns (IDotNugg.Base memory res) {
        res = _DOTNUGG.bytesToBase(_bases(seed % bases_.length));
    }
}
","keccak256":"0x76a145adf6cd3b7c2a9aec5277ef08f1495a22869b5db3e8e129cd722922ba5e","license":"MIT"},"contracts/auction/NuggMinter.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

import './base/Auctionable.sol';
import '../base/Launchable.sol';

import '../base/Seedable.sol';
import '../base/Epochable.sol';
import './interfaces/INuggMinter.sol';
import './periphery/IAuctionableImplementer.sol';

contract NuggMinter is INuggMinter, Auctionable, Launchable, Epochable, Seedable {
    IAuctionableImplementer internal _NUGGFT;

    INuggETH internal _NUGGETH;
    IWETH9 internal _WETH;

    // uint256 private constant _OFFSET = 42069;

    // uint256 private _pendingReward = _OFFSET;

    constructor() Epochable(25) {}

    /**
     * @inheritdoc Exchangeable
     */
    function WETH() internal view override returns (IWETH9 res) {
        res = _WETH;
    }

    /**
     * @inheritdoc Exchangeable
     */
    function NUGGETH() internal view override returns (INuggETH res) {
        res = _NUGGETH;
    }

    // function calculateCurrentAuctionId() internal view virtual returns (uint256 res);

    /**
     * @notice inializes contract outside of constructor
     * @inheritdoc Launchable
     */
    function launch(bytes memory data) public override {
        super.launch(data);
        (address nuggft, address nuggeth, address weth) = abi.decode(data, (address, address, address));
        _NUGGFT = IAuctionableImplementer(nuggft);
        _WETH = IWETH9(weth);
        _NUGGETH = INuggETH(nuggeth);
    }

    /**
     * @inheritdoc Auctionable
     */
    function _onWinnerClaim(Bid memory bid) internal override {
        super._onWinnerClaim(bid);
        _NUGGFT.onMinterClaim(bid.account, bid.auctionId);
    }

    /**
     * @inheritdoc Auctionable
     */
    function _onBidPlaced(Auction memory auction) internal override {
        super._onBidPlaced(auction);
        if (!seedExists(auction.auctionId)) {
            setSeed(auction.auctionId, calculateCurrentSeed());
        }
        _NUGGETH.depositRewards{value: auction.top.amount - auction.last.amount}(address(this));
    }

    /**
     * @inheritdoc Auctionable
     */
    function _auctionIsActive(Auction memory auction) internal view override returns (bool res) {
        return epochStatus(auction.auctionId) == EpochMath.Status.ACTIVE;
    }

    /**
     * @inheritdoc Auctionable
     */
    function _auctionIsOver(Auction memory auction) internal view override returns (bool res) {
        return epochStatus(auction.auctionId) == EpochMath.Status.OVER;
    }

    function currentAuction() public view virtual override returns (Auction memory res) {
        uint256 auctionId = currentEpochId();
        res = getAuction(auctionId);
    }

    /**
     * @notice gets unique base based on given epoch and converts encoded bytes to object that can be merged
     * Note: by using the block hash no one knows what a nugg will look like before it's epoch.
     * We considered making this harder to manipulate, but we decided that if someone were able to
     * pull it off and make their own custom nugg, that would be really fucking cool.
     */
    function calculateCurrentSeed() public view override returns (bytes32 res) {
        uint256 num = blocknumFromId(currentEpochId()) - 1;
        res = block_hash(num);
        require(res != 0, 'EPC:SBL');
        res = keccak256(abi.encodePacked(res, num));
    }
}
","keccak256":"0xe4ebe09e29a04bd16579696660bd407b2e221749b666f0369abf5bb6e2d5daa2","license":"MIT"},"contracts/auction/NuggSeller.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

import './base/Auctionable.sol';
import '../base/Launchable.sol';

import '../base/Seedable.sol';
import '../base/Epochable.sol';
import './interfaces/INuggSeller.sol';
import '../interfaces/INuggFT.sol';
import '../erc721/ERC721Holder.sol';

contract NuggSeller is INuggSeller, Auctionable, Launchable, ERC721Holder {
    INuggFT internal _NUGGFT;

    INuggETH internal _NUGGETH;
    IWETH9 internal _WETH;

    uint256 private _counter = 1;

    mapping(uint256 => Sale) _sales;

    mapping(uint256 => uint256) _lastSaleByToken;

    constructor() {}

    /**
     * @inheritdoc Exchangeable
     */
    function WETH() internal view override returns (IWETH9 res) {
        res = _WETH;
    }

    /**
     * @inheritdoc Exchangeable
     */
    function NUGGETH() internal view override returns (INuggETH res) {
        res = _NUGGETH;
    }

    function startSale(
        uint256 tokenId,
        uint256 length,
        uint256 floor
    ) external {
        Sale memory sale = Sale({sold: false, claimed: false, tokenId: tokenId, startblock: block.number, length: length, floor: floor, seller: msg_sender()});

        _NUGGFT.safeTransferFrom(sale.seller, address(this), tokenId);

        require(_NUGGFT.ownerOf(tokenId) == address(this), 'NS:START:0');
        uint256 saleId = _counter++;
        _sales[saleId] = sale;
        _lastSaleByToken[tokenId] = saleId;

        emit SaleStart(saleId, tokenId, length, floor);
    }

    function claimSale(uint256 saleId, Currency currency) external {
        Sale memory sale = _sales[saleId];

        require(block_num() > sale.startblock + sale.length, 'NS:CS:0');
        require(msg_sender() == sale.seller, 'NS:CS:1');
        require(sale.claimed == false, 'NS:CS:2');

        _sales[saleId].claimed = true;

        if (sale.sold) {
            uint256 amount = _bidsAmt[saleId][_topAddr[saleId]];
            uint256 royalties = (amount * 15) / 100;
            _NUGGETH.depositRewards{value: royalties}(address(this));
            giveCurrency(msg_sender(), amount - royalties, currency);
        } else {
            _NUGGFT.safeTransferFrom(address(this), sale.seller, sale.tokenId);
            emit SaleStop(saleId);
        }
    }

    function stopSale(uint256 saleId) public {
        Sale memory sale = _sales[saleId];
        require(msg_sender() == sale.seller, 'NS:SS:0');
        require(!sale.sold, 'NS:SS:1');
        require(!sale.claimed, 'NS:SS:2');

        _sales[saleId].claimed = true;

        _NUGGFT.safeTransferFrom(address(this), sale.seller, sale.tokenId);
        emit SaleStop(saleId);
    }

    /**
     * @notice inializes contract outside of constructor
     * @inheritdoc Launchable
     */
    function launch(bytes memory data) public override {
        super.launch(data);
        (address nuggft, address nuggeth, address weth) = abi.decode(data, (address, address, address));
        _NUGGFT = INuggFT(nuggft);
        _WETH = IWETH9(weth);
        _NUGGETH = INuggETH(nuggeth);
        _NUGGFT.setApprovalForAll(nuggft, true);
    }

    /**
     * @notice returns id of last and/or current sale for a given token id
     * @inheritdoc INuggSeller
     */
    function lastSaleByToken(uint256 tokenId) public view override returns (uint256 res) {
        return _lastSaleByToken[tokenId];
    }

    /**
     * @inheritdoc Auctionable
     */
    function _onWinnerClaim(Bid memory bid) internal override {
        super._onWinnerClaim(bid);
        _NUGGFT.onBuyerClaim(bid.account, _sales[bid.auctionId].tokenId);
    }

    /**
     * @inheritdoc Auctionable
     */
    function _onBidPlaced(Auction memory auction) internal override {
        require(auction.top.amount >= _sales[auction.auctionId].floor, 'NS:OBP:0');
        super._onBidPlaced(auction);
        if (auction.last.amount == 0) _sales[auction.auctionId].sold = true;
    }

    /**
     * @inheritdoc Auctionable
     */
    function _auctionIsActive(Auction memory auction) internal view override returns (bool res) {
        Sale memory sale = _sales[auction.auctionId];
        res = block_num() >= sale.startblock && block_num() < sale.startblock + sale.length;
    }

    /**
     * @inheritdoc Auctionable
     */
    function _auctionIsOver(Auction memory auction) internal view override returns (bool res) {
        Sale memory sale = _sales[auction.auctionId];
        res = block_num() > sale.startblock + sale.length;
    }
}
