    }

    struct VerticalExpanderVars {
        uint8 indexU;
        uint8 indexD;
        bool foundU;
        bool foundD;
        uint8 lenU;
        uint8 lenD;
        uint8 rowUCount;
        uint8 rowDCount;
        uint8 createdGroupsIndex;
    }
}
