pragma solidity 0.8.4;

/**
 * @title Base64
 * @author Nugg Labs - @danny7even & @dub6ix
 * @notice library for encoding bytes into base64
 */
library Base64 {
    string internal constant _TABLE = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/';
    string internal constant _BASE64_PREFIX_JSON = 'data:application/json;base64,';
    string internal constant _BASE64_PREFIX_DOTNUGG = 'data:image/dotnugg;base64,';
    string internal constant _BASE64_PREFIX_SVGs = 'data:image/svg+xml;base64,';

    /**
     * @notice wrapper for _encode for svg data
     * @param data bytes to encode
     * @return base64 string representation of input bytes, prefixed with json base64 prefix
     */
    function encodeSvg(bytes memory data) internal pure returns (string memory) {
        return string(abi.encodePacked(_BASE64_PREFIX_SVGs, _encode(data)));
    }

    /**
     * @notice wrapper for _encode for json data
     * @param data bytes to encode
     * @return base64 string representation of input bytes, prefixed with json base64 prefix
     */
    function encodeJson(bytes memory data) internal pure returns (string memory) {
        return string(abi.encodePacked(_BASE64_PREFIX_JSON, _encode(data)));
    }

    /**
     * @notice wrapper for _encode for dotnugg data
     * @param data bytes to encode
     * @return base64 string representation of input bytes, prefixed with dotnugg base64 prefix
     */
    function encodeDotNugg(bytes memory data) internal pure returns (string memory) {
        return string(abi.encodePacked(_BASE64_PREFIX_DOTNUGG, _encode(data)));
    }

    /**
     * @notice Encodes some bytes in base64
     * @param data bytes to encode
     * @return base64 string representation of input bytes
     * @dev Credit to Brecht Devos - <brecht@loopring.org> - under MIT license https://github.com/Brechtpd/base64/blob/main/base64.sol
     * @dev modified for solidity v8
     */
    function _encode(bytes memory data) private pure returns (string memory) {
        if (data.length == 0) return '';

        // load the table into memory
        string memory table = _TABLE;

        // multiply by 4/3 rounded up
        uint256 encodedLen = 4 * ((data.length + 2) / 3);

        // add some extra buffer at the end required for the writing
        string memory result = new string(encodedLen + 32);

        assembly {
            // set the actual output length
            mstore(result, encodedLen)

            // prepare the lookup table
            let tablePtr := add(table, 1)

            // input ptr
            let dataPtr := data
            let endPtr := add(dataPtr, mload(data))

            // result ptr, jump over length
            let resultPtr := add(result, 32)

            // run over the input, 3 bytes at a time
            for {

            } lt(dataPtr, endPtr) {

            } {
                dataPtr := add(dataPtr, 3)

                // read 3 bytes
                let input := mload(dataPtr)

                // write 4 characters
                mstore(resultPtr, shl(248, mload(add(tablePtr, and(shr(18, input), 0x3F)))))
                resultPtr := add(resultPtr, 1)
                mstore(resultPtr, shl(248, mload(add(tablePtr, and(shr(12, input), 0x3F)))))
                resultPtr := add(resultPtr, 1)
                mstore(resultPtr, shl(248, mload(add(tablePtr, and(shr(6, input), 0x3F)))))
                resultPtr := add(resultPtr, 1)
                mstore(resultPtr, shl(248, mload(add(tablePtr, and(input, 0x3F)))))
                resultPtr := add(resultPtr, 1)
            }

            // padding with '='
            switch mod(mload(data), 3)
            case 1 {
                mstore(sub(resultPtr, 2), shl(240, 0x3d3d))
            }
            case 2 {
                mstore(sub(resultPtr, 1), shl(248, 0x3d))
            }
        }

        return result;
    }
}
","keccak256":"0xd4ea3e92ba51b6a1a18e8935b18c99f4aeffc16dbe8193e38d1434703cc3bef4","license":"MIT"},"contracts/libraries/EpochMath.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

library EpochMath {
    struct State {
        uint256 genesisBlock;
        uint8 interval;
    }

    enum Status {
        OVER,
        ACTIVE,
        PENDING
    }

    struct Epoch {
        uint256 id;
        uint256 startblock;
        uint256 endblock;
        Status status;
    }

    function getEpoch(
        EpochMath.State memory state,
        uint256 id,
        uint256 blocknum
    ) internal pure returns (EpochMath.Epoch memory res) {
        res = EpochMath.Epoch({
            id: id,
            startblock: getStartBlockFromId(state, id),
            endblock: getEndBlockFromId(state, id),
            status: getStatus(state, id, blocknum)
        });
    }

    function getStatus(
        State memory state,
        uint256 id,
        uint256 blocknum
    ) internal pure returns (Status res) {
        if (getIdFromBlocknum(state, blocknum) == id) res = Status.ACTIVE;
        else if (getEndBlockFromId(state, id) < blocknum) res = Status.OVER;
        else res = Status.PENDING;
    }

    /**
     * @dev #TODO
     * @return res
     */
    function getStartBlockFromId(State memory state, uint256 id) internal pure returns (uint256 res) {
        res = id * state.interval + state.genesisBlock;
    }

    /**
     * @dev #TODO
     * @return res
     */
    function getEndBlockFromId(State memory state, uint256 id) internal pure returns (uint256 res) {
        res = getStartBlockFromId(state, id + 1) - 1;
    }

    function getIdFromBlocknum(State memory state, uint256 blocknum) internal pure returns (uint256 res) {
        res = (blocknum - state.genesisBlock) / state.interval;
    }
}
","keccak256":"0x36457d02197ece0d5d2771cb3da03ed019aedf5cce5509dc518d716c9b4c801e","license":"MIT"},"contracts/libraries/Exchange.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.4;
import '../libraries/Address.sol';

import '../interfaces/IWETH9.sol';
import '../weth/interfaces/INuggETH.sol';

library Exchange {
    using Address for address payable;

    function take_eth(address account, uint256 amount) internal {
        require(msg.value == amount && msg.sender == account, 'EX:TE:0');
    }

    function give_eth(address payable account, uint256 amount) internal {
        account.sendValue(amount);
    }

    function give_weth(
        IWETH9 weth,
        address account,
        uint256 amount
    ) internal {
        weth.deposit{value: amount}();
        weth.transfer(account, amount);
    }

    function take_weth(
        IWETH9 weth,
        address account,
        uint256 amount
    ) internal {
        require(weth.allowance(account, address(this)) >= amount, 'EXC:TW:0'); // only for better handling on front end
        weth.transferFrom(account, address(this), amount);
        weth.withdraw(amount);
    }

    function give_nuggeth(
        INuggETH nuggeth,
        address account,
        uint256 amount
    ) internal {
        nuggeth.depositTo{value: amount}(account);
    }

    function take_nuggeth(
        INuggETH nuggeth,
        address account,
        uint256 amount
    ) internal {
        require(nuggeth.allowance(account, address(this)) >= amount, 'EXC:TW:0'); // only for better handling on front end
        nuggeth.withdrawFrom(account, amount);
    }
}
