pragma solidity 0.8.4;

interface INuggFT {
    event PreMint(uint256 tokenId, uint256[] items);

    event PopItem(uint256 tokenId, uint256 itemId);

    event PushItem(uint256 tokenId, uint256 itemId);

    event Mint(uint256 epoch, address account, uint256 eth);

    event Commit(uint256 tokenid, address account, uint256 eth);

    event Offer(uint256 tokenid, address account, uint256 eth);

    event Claim(uint256 tokenid, uint256 endingEpoch, address account);

    event Swap(uint256 tokenid, address account, uint256 eth);

    event CommitItem(uint256 sellingTokenId, uint256 itemId, uint256 buyingTokenId, uint256 eth);

    event OfferItem(uint256 sellingTokenId, uint256 itemId, uint256 buyingTokenId, uint256 eth);

    event ClaimItem(uint256 sellingTokenId, uint256 itemId, uint256 buyingTokenId, uint256 endingEpoch);

    event SwapItem(uint256 sellingTokenId, uint256 itemId, uint256 eth);

    event Genesis();

    function swapItem(
        uint256 tokenid,
        uint256 floor,
        uint256 itemid
    ) external;

    function xnugg() external view returns (address payable);

    function genesis() external view returns (uint256 res);

    function epoch() external view returns (uint256 res);

    function delegate(uint256 tokenid) external payable;

    function delegateItem(
        uint256 sellerTokenId,
        uint256 itemid,
        uint256 buyerTokenId
    ) external payable;

    function mint(uint256 tokenid) external payable;

    function commit(uint256 tokenid) external payable;

    function commitItem(
        uint256 sellerTokenId,
        uint256 itemid,
        uint256 buyerTokenId
    ) external payable;

    function offer(uint256 tokenid) external payable;

    function offerItem(
        uint256 sellerTokenId,
        uint256 itemid,
        uint256 buyerTokenId
    ) external payable;

    function claim(uint256 tokenid, uint256 endingEpoch) external;

    function claimItem(
        uint256 sellerTokenId,
        uint256 itemid,
        uint256 buyerTokenId,
        uint256 endingEpoch
    ) external;

    function swap(uint256 tokenid, uint256 floor) external;

    function getOfferByAccount(
        uint256 tokenid,
        uint256 index,
        address account
    ) external view returns (uint256 eth);

    function getActiveSwap(uint256 tokenid)
        external
        view
        returns (
            address leader,
            uint256 eth,
            uint256 _epoch,
            bool isOwner
        );
}

contracts/src/libraries/EpochLib.sol.content:
import './ShiftLib.sol';

library EpochLib {
    using ShiftLib for uint256;

    /**
     * @dev #TODO
     * @return res
     */
    function activeEpoch(uint256 genesis) internal view returns (uint256 res) {
        res = toEpoch(genesis, block.number);
    }

    /**
     * @notice gets unique base based on given epoch and converts encoded bytes to object that can be merged
     * Note: by using the block hash no one knows what a nugg will look like before it's epoch.
     * We considered making this harder to manipulate, but we decided that if someone were able to
     * pull it off and make their own custom nugg, that would be really fucking cool.
     */
    function calculateSeed(uint256 genesis) internal view returns (uint256 res, uint256 epoch) {
        epoch = toEpoch(genesis, block.number);
        uint256 startblock = toStartBlock(genesis, epoch);
        bytes32 bhash = blockhash(startblock - 1);
        require(bhash != 0, 'EPC:SBL');
        res = uint256(keccak256(abi.encodePacked(bhash, epoch, address(this))));
    }

    function interval() internal pure returns (uint256 res) {
        res = 25;
    }

    /**
     * @dev #TODO
     * @return res
     */
    function toStartBlock(uint256 genesis, uint256 epoch) internal pure returns (uint256 res) {
        res = (epoch * interval()) + genesis;
    }

    /**
     * @dev #TODO
     * @return res
     */
    function toEndBlock(uint256 genesis, uint256 epoch) internal pure returns (uint256 res) {
        res = toStartBlock(genesis, epoch + 1) - 1;
    }

    function toEpoch(uint256 genesis, uint256 blocknum) internal pure returns (uint256 res) {
        res = (blocknum - genesis) / interval();
    }
}

contracts/src/libraries/ItemLib.sol.content:
import 'hardhat/console.sol';

import './ShiftLib.sol';
import './EpochLib.sol';

library ItemLib {
    event PreMint(uint256 tokenId, uint256[] items);
    event PopItem(uint256 tokenId, uint256 itemId);
    event PushItem(uint256 tokenId, uint256 itemId);
    /**
     * @dev Emitted when `value` tokens of token type `id` are transferred from `from` to `to` by `operator`.
     */
    event TransferSingle(address indexed operator, address indexed from, address indexed to, uint256 id, uint256 value);

    /**
     * @dev Equivalent to multiple {TransferSingle} events, where `operator`, `from` and `to` are the same for all
     * transfers.
     */
    event TransferBatch(
        address indexed operator,
        address indexed from,
        address indexed to,
        uint256[] ids,
        uint256[] values
    );

    using ShiftLib for uint256;

    struct Storage {
        mapping(uint256 => uint256) tokenData;
        mapping(uint256 => uint256) protocolItems;
    }

    function infoOf(Storage storage s, uint256 tokenId)
        internal
        view
        returns (
            uint256 base,
            uint256 size,
            uint256[] memory items
        )
    {
        uint256 data = s.tokenData[tokenId];
        items = data.items();
        size = data.size();
        base = data.base();
    }

    function premint(
        Storage storage s,
        uint256 tokenId,
        uint256 genesis
    ) internal {
        (uint256 itemData, uint256 epoch) = EpochLib.calculateSeed(genesis);

        require(itemData != 0, '721:MINT:0');
        require(epoch == tokenId, '721:MINT:1');

        itemData = itemData;

        uint256[] memory items = mint(s, tokenId, itemData);

        emit PreMint(tokenId, items);

        uint256[] memory amounts = new uint256[](items.length);
        for (uint256 i = 0; i < amounts.length; i++) {
            amounts[i] = 1;
        }

        emit TransferBatch(address(this), address(0), msg.sender, items, amounts);
    }

    function mint(
        Storage storage s,
        uint256 tokenId,
        uint256 data
    ) internal returns (uint256[] memory items) {
        require(s.tokenData[tokenId] == 0, 'IL:M:0');

        data = data.base(data.base() % 20).size(0x4);

        s.tokenData[tokenId] = data;

        return data.items();
    }

    function pop(
        Storage storage s,
        uint256 tokenId,
        uint256 itemId
    ) internal {
        uint256 data = s.tokenData[tokenId];

        require(data != 0, '1155:STF:0');

        (data, , ) = data.popFirstMatch(uint8(itemId));

        s.tokenData[tokenId] = data;

        s.protocolItems[itemId]++;

        emit PushItem(tokenId, itemId);
        emit TransferSingle(address(this), msg.sender, address(this), itemId, 1);
    }

    function push(
        Storage storage s,
        uint256 tokenId,
        uint256 itemId
    ) internal {
        uint256 data = s.tokenData[tokenId];
        require(data != 0, '1155:STF:0');

        require(s.protocolItems[itemId] > 0, '1155:SBTF:1');

        s.protocolItems[itemId]++;

        (data, ) = data.pushFirstEmpty(uint8(itemId));

        s.tokenData[tokenId] = data;

        emit PushItem(tokenId, itemId);
        emit TransferSingle(address(this), address(this), msg.sender, itemId, 1);
    }
}

